int a[200020];
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int now = 0;
        int ans = 0;
        for(int i=0;i<n;++i){
            a[i] = a[i+n] = colors[i];
        }
        a[2*n] = a[0];

        int startId = 0;
        for(int i=1;i<=n;++i){
            if(a[i] == a[i-1]){
                startId = i;
                break;
            }
        }
        if(startId == 0)return n;
        now = startId;
        for(int i=startId; i < startId + n;++i){
            if(a[i] == a[i+1]){
                ans += max(0, i-now+1 - (k-1));
                now = i+1;
            }
        }

        return ans;
    }
};
