class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int cnt=1,ans=0;
        for(int i=0;i+1<k;i++) 
        if(colors[i] == colors[i+1]) break;
        else cnt++;
        for(int i=n-1;i>=0;i--){
            if(colors[i] == colors[(i+1)%n]) cnt=1;
            else cnt++;
            if(cnt >= k) ans++;
        }
        return ans;
    }
};
