class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        res = 0
        start = 0
        colors = colors + colors[:k-1]
        for i in range(1, len(colors)):
            if colors[i] == colors[i-1]:
                cnt = i - start
                res += max(cnt-k+1, 0)
                start = i
        cnt = len(colors) - start
        res += max(cnt-k+1, 0)
        return res
