class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
       int n = colors.size();
        if (k > n) return 0;

        int ans = 0;
        int count = 1;
        int curr = colors[0];

        for (int i = 1; i < n + k - 1; ++i) {
            int next = colors[i % n];
            if (next != curr) {
                count++;
            } else {
                count = 1;
            }

            if (count >= k) {
                ans++;
            }

            curr = next;
        }

        return ans; 

    }
};
