class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        vector<int>a;
        vector<int>d=c;
        for(int i=0;i<n;i++){
            c.push_back(d[i]);
        }
        // a.push_back(0);
        for(int i=0;i<c.size()-1;i++){
            if(c[i]!=c[i+1]){
                a.push_back(1);
            }
            else{
                a.push_back(0);
            }
        }
        // for(auto x:a){
        //     cout<<x<<" ";
        // }
        // cout<<"\n";
        vector<int>pre(a.size()+1,0);
        for(int i=0;i<a.size();i++){
            pre[i+1]=a[i]+pre[i];
        }
        // for(auto x:pre){
        //     cout<<x<<" ";
        // }
        // cout<<"\n";
        int ans=0;
        // cout<<pre.size()<<"\n";
        // cout<<k+n-1<<"\n";
        for(int i=0;i<n;i++){
            // cout<<pre[k+i]<<" "<<pre[i]<<"\n";
            if(pre[k-1+i]-pre[i]==k-1){
                ans++;
            }
        }
        return ans;
        return 0;
    }
};
