from typing import List

class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        if k > n:
            return 0
        diff = [0] * n
        for i in range(n):
            if colors[i] != colors[(i + 1) % n]:
                diff[i] = 1
        curr = sum(diff[:k-1])
        count = 0
        for i in range(n):
            if curr == k - 1:
                count += 1
            curr -= diff[i]
            curr += diff[(i + k - 1) % n]

        return count
