class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int i=0, j=1;
        int count=0;
        for(; j<2*n;)
        {
            // cout << "j " << j%n << " " << (j-1)%n << endl;
            if (colors[(j%n)]!=colors[(j-1)%n])
            {
                // cout << j << " " << j-1 << endl;
                if ((j-i+1)==k)
                {
                    // cout << "jj " << j << endl;
                    count++;
                    i++;
                    if (i>=n) break;
                    // break;
                }
                j++;
            }
            else
            {
                i=j;
                // cout << "i " << i << endl;
                if (i>=n) break;
                j++;
            }
        }
        // if (i==n) return 0;
        // cout << i << " " << j;
        return count;
    }
};
