class Solution {
public:
    typedef unsigned long long ull;
    ull h1[100005], h2[100005], p[100005], h[200005];
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        string s = " ";
        int n = colors.size();
        for (auto& x : colors) {
            s += (char)(x + '0');
        }
        s += s.substr(1);
        string s1 = " ";
        char c = '0';
        for (int i = 1; i <= k; i++) {
            s1 += c;
            if (c == '0') c = '1';
            else c = '0';
        }
        string s2 = " ";
        c = '1';
        for (int i = 1; i <= k; i++) {
            s2 += c;
            if (c == '0') c = '1';
            else c = '0';
        }
        h1[0] = h2[0] = 0;
        p[0] = 1;
        for (int i = 1; i <= k; i++) {
            h1[i] = h1[i - 1] * 131 + s1[i];
            h2[i] = h2[i - 1] * 131 + s2[i];
            p[i] = p[i - 1] * 131;
        }
        long long ans = 0;
        h[0] = 0;
        for (int i = 1; i <= n + k - 1; i++) {
            h[i] = h[i - 1] * 131 + s[i];
            if (i >= k) {
                int l = i - k + 1, r = i;
                ull t = h[r] - h[l - 1] * p[k];
                if (t == h1[k] || t == h2[k]) ans++;
            }
        }
        return ans;
    }
};
