class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        for(int i = 0 ; i < n ; i++)
        {
            int flag = 0;
            for(int j = i + 1, p = 0 ; p < (k-1) ; j++,p++)
            {
                if(colors[j%n] == colors[(j-1)%n])
                {
                    flag = 1;
                    i = j-1;
                    break;
                }
            }

            if(flag == 0)
            {
                int w = i + k - 1;
                while(i < n && colors[w%n] != colors[(w-1)%n])
                {
                    ans++;
                    w++;
                    i++;
                }

                i = i -1;
            }
        }

        return ans;
    }
};
