class Solution {
public:

        int numberOfAlternatingGroups(vector<int>& colorArray, int threshold) {
    int arraySize = colorArray.size();
    int groupCount = 0;

    if (threshold == 1) {
        return arraySize;
    }

    int currentAlternatingLength = 1;
    int index = 1;

    while (index < arraySize + threshold - 1) {
        int currentIndex = index;
        int previousIndex = index - 1;

        if (currentIndex >= arraySize) {
            currentIndex -= arraySize;
        }

        if (previousIndex >= arraySize) {
            previousIndex -= arraySize;
        }

        if (colorArray[currentIndex] != colorArray[previousIndex]) {
            ++currentAlternatingLength;
        } else {
            currentAlternatingLength = 1;
        }

        if (currentAlternatingLength >= threshold) {
            ++groupCount;
        }

        ++index;
    }

    return groupCount;

    }
};
