public class Solution {
    public int NumberOfAlternatingGroups(int[] colors, int k) {
        var arr = colors.Concat(colors).Concat(colors).ToArray();
        int n = colors.Length;
        int l = n - k + 1, r = n, cnt = 0;
        for (int i = l + 1; i < r; ++i) if (arr[i] == arr[i-1] || arr[i] == arr[i+1]) ++cnt;
        int ans = cnt == 0 ? 1 : 0;
        while (r < 2 * n - 1) {
            ++l;
            if (arr[l] == arr[l-1] || arr[l] == arr[l+1]) --cnt;
            if (arr[r] == arr[r-1] || arr[r] == arr[r+1]) ++cnt;
            if (cnt == 0) ++ans;
            ++r;
        }
        return ans;
    }
}
