class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
 vector<int>st;

          for(int i:c)
              st.push_back(i);
        for(int i=0;i<k-1;i++) 
            st.push_back(c[i]);
       long long ans=0;
        int cnt=0;


        bool check=true;
         int n=st.size();
        int tot = 0;
        for(int i=0;i<n;i++) {
            int cnt=1;
            for(int j=i+1;j<n;j++) {
                if(st[j]==st[j-1]){
                    i=j-1;
                    break;
                }
                else{
                    cnt++;
                }
                if(j==n-1) {
      check=false;
                    break;
                } 
            }

            ans += max(0, cnt - k + 1);
            if(check==false)
                break;
        }
        return ans;
}
};
