class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
       int len=c.size();
       int cnt=0;
       if(k==1) return len;

       int clen=1;
       for(int i=1;i<len+k-1;++i)
       {
        int ci=i%len;
        int pi=(i-1)%len;

        if(c[ci]!=c[pi])
        {
            ++clen;
        }
        else
        {
            clen=1;
        }
        if(clen>=k)
        {
            ++cnt;
        }
       }
       return cnt;
    }
};
