class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> req;
        int nx = colors.size();
        for(int i = 0; i<nx; i++) req.push_back(colors[i]);
        for(int i = 0; i<k; i++) req.push_back(colors[i]);
        int n = req.size();
        int cnt = 0;
        vector<int> pref(n + 1, 0);
        for (int i = 1; i < n; ++i) {
            pref[i] += pref[i - 1];
            pref[i] += abs(req[i] - req[i-1]);
        }
        vector<int> reqq;
        for(int i = k; i<n; i++) reqq.push_back(pref[i] - pref[i - k + 1]); 
        for(auto it: reqq) cnt += (it == k-1);
        return cnt;
    }
};
