class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        int count = 0;
        for (int i = 1; i < k; i++) {
            if (colors[i] != colors[i - 1]) {
                count++;
            }
        }
        if (count == (k - 1)) {
            ans++;
        }
        //cout<<ans<<endl;
        for (int i = 1; i < n; i++) {
            if (colors[i - 1] != colors[i]) {
                count--;
            }
            if (colors[(i + k - 1) % n] != colors[(i + k - 2) % n]) {
                count++;
            }
            if (count == (k - 1)) {
                ans++;
            }
        }

        return ans;
    }
};
