class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums,int k) {
        int n=nums.size();
        int i=0,j=1;
        int ans=0,total=0;
        vector<int> t(2*n,0);
        for(int i=0;i<2*n;i++){
            t[i]=nums[i%n];
        }
        while(j<n){

            int f=0;
            while(j<n and nums[j]!=nums[j-1]){
                j++;
                f++;;
            }

            if(f>=k-1){
                ans+=f-k+2;
                // i=j;
            }

            j++;
        }
        j=1,i=0;
          while(j<2*n){
            int f=0;
            while(j<2*n and t[j]!=t[j-1]){
                j++;
                f++;
            }

            if(f>=k-1 ){
                total+=f-k+2;
                // i=j;
            }

            j++;
        }

        return total-ans;
    }
};
