class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>nums=colors;
        for(int i=0;i<colors.size();i++)nums.push_back(colors[i]);
        vector<int>v;
        for(int i=0;i<nums.size()-1;i++){
            if(nums[i]==nums[i+1])v.push_back(i);
        }
        int ans=0;
        for(int i=0;i<colors.size();i++){
            int l=lower_bound(v.begin(),v.end(),i)-v.begin();
            if(v.size()==l)ans++;
            else{
                if(i+k-1<=v[l])ans++;
            }
        }return ans;
    }
};
