class Solution {
    public function numberOfAlternatingGroups($colors, $k) {
        $N = count($colors);
        $count = 0;

        if ($k == 1) {
            return $N;
        }

        $alternating_length = 1;
        $i = 1;

        while ($i < $N + $k - 1) {
            $current = $i;
            $previous = $i - 1;

            if ($current >= $N) {
                $current -= $N;
            }
            if ($previous >= $N) {
                $previous -= $N;
            }

            if ($colors[$current] != $colors[$previous]) {
                ++$alternating_length;
            } else {
                $alternating_length = 1;
            }

            if ($alternating_length >= $k) {
                ++$count;
            }

            ++$i;
        }

        return $count;
    }
}

// Example usage:
$solution = new Solution();
$colors = [1, 2, 1, 2, 1];
$k = 2;
echo $solution->numberOfAlternatingGroups($colors, $k); // Output will depend on the input array and value of k
