class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a1, int k) {
        vector<int> a=a1;
        int n=a1.size();
        for(auto e:a1){
            a.push_back(e);
        }
        for(auto &e:a){
            if(e==0){
                e=-1;
            }
        }
        int c=0;
        int ans=0;
        int v=k-1;
        for(int i=0;i<(k-1);i++){
            if((a[i]+a[i+1])==0){
                c++;
            }
        }
        if(c==v) ans++;
        for(int i=1;i<n;i++){
            if((a[i-1]+a[i])==0){
                c--;
            }
            if((a[i+k-1]+a[i+k-2])==0){
                c++;
            }
            if(c==v) ans++;
        }

        return ans;


    }
};
