class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        l = 1
        res = 0
        n = len(colors)

        for i in range(1, n):
            if colors[i] != colors[i - 1]:
                l += 1
                if l >= k:
                    res += 1
            else:
                l = 1

        for i in range(k - 1):
            if colors[i] != colors[i - 1]:
                l += 1
                if l >= k:
                    res += 1
            else:
                l = 1
                break

        return res
