    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n = nums.size(), ct = 0;

        if(k == 1)
            return n;

        int al = 1, i = 1;
        while(i<n+k-1){
            int ci = i;
            int pi = i-1;
            if(ci >= n){
                ci -= n;
            }
            if(pi >= n){
                pi -=n;
            }
            if(nums[ci] != nums[pi]){
                ++al;
            }
            else{
                al = 1;
            }

            if(al >= k){
                ++ct;
            }

            i++;
        }

        return ct;
    }
};
