class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        bool flag=false;
        int ind = 0, ans=0;
        while(ind<n){
            flag=false;
            for(int i=ind;i<ind+k-2;i++){
                if(colors[i%n]!=colors[(i+2)%n]){
                    ind=i+1;
                    flag=true;
                    break;
                }
                if(colors[i%n]==colors[(i+1)%n]){
                    ind=i+1;
                    flag=true;
                    break;
                }
            }
            int newind = ind+k;
            if(flag==true) continue;
            ans++;
            while(newind%n!=k-1&&colors[newind%n]==colors[(newind-2+n)%n]&&colors[newind%n]!=colors[(newind-1)%n]){
                newind++;
                ans++;
            }
            ind=newind;
        }
        return ans;
    }
};
