class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> c = colors;
        int n = colors.size();
        for(auto it : colors) c.push_back(it);
        int ans = 0;
        for(int i=0;i<n+k-1;i++)
        {
            int ind = i , len = 1;
            while(ind+1<n+k-1 && c[ind+1]!=c[ind])
            {
                ind++;
                len++;
            }
            i = ind;
            ans+=max(0,len-k+1);
        }
        return ans;
    }
};
