class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i =0;i<k-1;i++){
            colors.push_back(colors[i]);
        }

        int ans = 0;
        int check = 0;
        int i =0;
        n = colors.size();
        while(i<n-1){
            if(colors[i] != colors[i+1]){
                check++;
            }
            else{
                check = 0;
            }

            if(check == k-1){
                ans++;
                check--;
                // cout << i << " ";
            }
            i++;
        }
        return ans;
    }

};
