class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        vector<int>y=colors;

        for(int i=0;i<k;i++)
        {
            y.push_back(colors[i]);
        }

        int n=y.size();

        long long ans=0;

        vector<int>op(n+1,0);


        for(int i=1;i<y.size();i++)
        {
            op[i]=op[i-1];
            op[i]+=abs(y[i]-y[i-1]);
        }

        for(int j=k;j<y.size();j++)
        {
            if(op[j]-op[j-k+1]==(abs(k-1)))
            {
                ans++;
            }
        }

        return ans;






    }
};
