class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int res = 1, ans = 0;
        int n = col.size();


        for(int i=1; i<n+k-1; i++){
            int curr = col[i%n];
            int prev = col[(i-1)%n];

            if(curr == prev) res = 1;
            else res++;

            ans += (res >= k);
        }

        return ans;
    }
};
