class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++)
            colors.push_back(colors[i]);

        int ans = 0;
        for(int i=0;i<colors.size();i++){
            int start = i;
            while(i<colors.size()-1 && colors[i] !=colors[i+1])
                i++;
            // cout<<start<<" "<<i<<"\n";
            if(i-start>=k-1)
                ans += i-start-k+2;
        }
        // cout<<"*\n";
        return ans;
    }
};
