class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
           deque<int>q;
           for(int i=0;i<k-1;i++) a.push_back(a[i]);
           int ans=0;
           q.push_back(a[0]);
          for(int i=1;i<a.size();i++){
               if(a[i]==q.back()){
                     while(!q.empty()) q.pop_front();
               }
               if(q.size()==k) q.pop_front();
               q.push_back(a[i]);
               if(q.size()==k)ans++;
          }
        return ans;

    }
};
