class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        N = len(colors)
        ar = colors + colors
        for i in range(0, len(ar), 2):
            ar[i] = 1 - ar[i]
        for i in range(len(ar) - 1):
            ar[i + 1] += ar[i]

        ans = 0
        for start in range(N):
            segmentSum = ar[start + k - 1]
            if start: segmentSum -= ar[start - 1]
            if segmentSum == 0 or segmentSum == k:
                ans += 1
        return ans
