class Solution {
public:
    void fun1(vector<int>& c, int m) {
        for (int p = 1; p < m; p++) {
            c.push_back(c[p - 1]);
        }
    }
    int fun2(vector<int>& c, int m) {
        int size = c.size();
        int re = 0;
        int ans = 1;
        int p = 1; while (p < size) {
         if(c[p] != c[p - 1])
         ans++;
           else{
            if(ans>=m)
            re += ans-m+1;

           ans = 1;
            }
            p++;
        }
        re += (ans >= m) ? ans - m + 1 : 0;
        return re;
    } int numberOfAlternatingGroups(vector<int>& c, int m) {
        fun1(c, m);
        return fun2(c, m);
    }
};
