class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(),ctr=0;
        vector<int>color(2*n);
        for(int i=0;i<2*n;i++){
            color[i]=colors[i%n];
        }

        vector<int>dp(2*n,1);

        for(int i=1;i<2*n;i++){
            if(color[i]!=color[i-1])dp[i]=dp[i-1]+1;

        }
        for(int i=k-1;i<k-1+n;i++){
            if(dp[i]>=k)ctr++;

        }
        return ctr;
    }
};
