class Solution:
    def numberOfAlternatingGroups(self, c: List[int], k: int) -> int:
        n = len(c)
        alt = [0]*n
        for i in range(n):
            if c[i]!=c[i-1] and c[i]!=c[(i+1)%n]:
                alt[i] = 1
        k -= 2
        #print(alt)
        for i in range(k-1):
            alt.append(alt[i])
            n += 1
        sm = 0
        #print(alt)
        for i in range(k):
            sm += alt[i]
        ans = 1*(sm==k)
        for i in range(1, n-k+1):
            sm -= alt[i-1]
            sm += alt[i+k-1]
            ans += 1*(sm==k)
        return ans
