class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
         int n = arr.size();
        int num_found = 0;
        int ikl = 0;
        int jmn = 1;

        while (jmn < n + k && ikl < n) {
            int temp_j = jmn;
            int temp_end = temp_j % n;

            while (arr[temp_end] != arr[(temp_end - 1 + n) % n] && (temp_j < (n + k - 1))) {
                temp_j++;
                temp_end = temp_j % n;
            }

            num_found += max(temp_j - ikl - k + 1, 0);
            ikl = temp_j;
            jmn = ikl + 1;
        }

        return num_found;
    }
};
