class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        vector<int> pre(n+k+1);
        for(int i=0;i<n+k-2;i++){
            if ((c[(i-1+n)%n]==c[(i+1)%n] and c[(i+1)%n]!=c[i%n])) pre[i+1]=pre[i]+1;
        }
        // for (auto i:pre) cout<<i<<" ";
        // // for (int i=0;i<k;i++) pre[n+]
        // cout<<endl;
        int ans=0;
        for (int i=0;i<n;i++){
            if (pre[i+k-1]-pre[i]>=k-2) ans++;
        }

        return ans;
    }
};
