#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& A, int tar) {
        int i = 0, j = 1, n = A.size(), fnd = 0;
        while (j < n + tar && i < n) {
            int x = j, y = x % n;
            while (A[y] != A[(y + n - 1) % n] && x < (n + tar - 1)) {
                x++;
                y = x % n;
            }
            fnd += max(x - tar - i + 1, 0);
            i = x;
            j = i + 1;
        }
        return fnd;
    }
};
