class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int l = 0;
        int r = 0;
        vector<int> v = a;
        for(int i = 0 ; i < k - 1 ; i++){
            v.push_back(v[i]);
        }
        int n = v.size();
        int ans = 0;
        int c= 0;
        while(r < n && l <= r){
            if(r != 0 && v[r] == v[r - 1]){
                l = r;
            }
            else{
                if(r - l + 1 == k){
                    ans ++;
                    l++;
                }
            }
            r++;
        }
        return ans;
    }
};
