class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int res = 0;
        int n = colors.size();
        int acc = 1;
        int prev = colors[0];
        for(int i = 1;i < n + k - 1;i++){
            if(colors[i % n] != prev){
                acc++;
            }
            else{
                acc = 1;
            }

            while(acc == k){
                res++;
                acc--;
            }

            prev = colors[i % n];
        }
        return res;
    }
};
