class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int i=0,j=1,ans=0;
        int n=arr.size();
        while(j<n+k && i<n){
            int temp1=j;
            int temp2=temp1%n;
            while(arr[temp2]!=arr[(temp2-1+n)%n] && temp1<n+k-1){
                temp1++;
                temp2=temp1%n;
            }
             ans += max(temp1 - i - k + 1, 0);
            i = temp1;
            j = i+1;
        }
        return ans;
    }
};
