class Solution {
public:
    bool cmp(int &a ,int &b){
        return a>b;
    }
    int numberOfAlternatingGroups(vector<int>& color, int k) {
        int ans = 0 , i = 0 , j = 1 , n = color.size();
        int mod = 1e9+7;
        bool flag = false;

        // int gains = 0;
        // int i = 0;

    // sort(color.begin() , color.end());

        while( i<n && j<n+k)
        {
            int start = j;
            int end= start % n;


            while(color[end] != color[(end-1+n) % n] && n+k-1>start) {
                start++;
                end = (start %n);
                flag = true;
            }
            ans += max(start-i-k+1,0);

            if(flag)flag = false;
            i = start; j = i + 1;

        }
        return ans;
    }
};
