class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {

        int first=0,last=0,total=0;
        int n=colors.length;

      for(int i=0;i<n;i++){

          int cur=colors[i];
          int c=1;

          while(i+1<n && colors[i+1]!=cur){
              cur=colors[i+1];
              i++;
              c++;
          }



          if(first==0){
              first=c;
          }
          else if(i==n-1){
             last=c;
          } 

          else {
              total+=Math.max(0,c-k+1);
          }


      }

      if(first==n && colors[n-1]!=colors[0] ){
          // System.out.println("test"+" "+first+" "+(2*n-(k+1)));
         return (n-k+1)+(k-1);
      }  




        // System.out.println(first+" h"+last);

      if(colors[n-1]!=colors[0]){
           total+=Math.max(0,first+last+1-k);
      }
      else{
          total+=Math.max(0,first-k+1);
          total+=Math.max(0,last-k+1);
      }  





        return total;

    }
}
