class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        map<int, int> mods_red, mods_blue;
        for (int i = 0; i < k - 1; ++i) colors.push_back(colors[i]);
        for (int i = 0; i < k; ++i) {
            if (colors[i] == 1) mods_red[i & 1]++;
            else mods_blue[i & 1]++;
        }
        auto check = [&]() -> bool {
            // 1 0
            int count_red = mods_red[1], count_blue = mods_blue[0];
            if (abs(count_red - count_blue) <= 1 && count_red + count_blue == k) return true;
            count_red = mods_red[0], count_blue = mods_blue[1];
            if (abs(count_red - count_blue) <= 1 && count_red + count_blue == k) return true;
            return false; 
        };
        int ans = check();
        for (int i = k; i < colors.size(); ++i) {
            if (colors[i - k] == 1) mods_red[(i - k) & 1]--;
            else mods_blue[(i - k) & 1]--;
            if (colors[i] == 1) mods_red[i & 1]++;
            else mods_blue[i & 1]++;
            ans += check();
        }
        return ans;
    }
};
