class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
           int i = 0, j = 1;
            int final = 0;
          int n = colors.size();
            vector<int> arra = colors;
        int z=0; while(z!=n){ arra.push_back(colors[z]);z++;}
        // for(int i = 0; i < n; i++){
        //     arra.push_back(colors[i]);
        // }
        while(i < n){

            while(i < n && j - i + 1 < k){
                if(arra[j]==arra[j-1]){i=j;}
                j++;
            }
            if(i >= n){
                break;
            }

            if(arra[j] != arra[j-1]){
                final++;
                i++;
                j++;
            }
            else{
                i = j;
                j++;
            }
        }

        return final;
    }
};
