class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
       Deque<Integer> dq = new ArrayDeque<>();
        int ans = 0;

        for(int i = 0; i < colors.length + k - 1; i++) {
            if(dq.isEmpty()) {
                dq.addLast(colors[i%colors.length]);
                continue;
            }

            if(dq.getLast() == colors[i%colors.length]) {
                dq = new ArrayDeque<>();
                dq.addLast(colors[i%colors.length]);
            } else {
                dq.addLast(colors[i%colors.length]);
            }

            if(dq.size() >= k) ans++;
        }


        return ans;
    }
}
