class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] newcolors = new int[n * 2];
        for (int i = 0; i < n; i++) {
            newcolors[i] = colors[i];
        }
        for (int i = n; i < 2 * n; i++) {
            newcolors[i] = colors[i - n];
        }
        int cnt = 0;
        for (int i = 1; i < k - 1; i++) {
            if (newcolors[i] != newcolors[i - 1] && newcolors[i] != newcolors[i + 1]) {
                cnt++;
            }
        }
        int res = 0;
        if (cnt == k - 2) {
            res++;
        }
        for (int i = 1; i < n; i++) {
            if (newcolors[i] != newcolors[i - 1] && newcolors[i] != newcolors[i + 1]) {
                cnt--;
            }
            if (newcolors[i + k - 2] != newcolors[i + k - 3] && newcolors[i + k - 2] != newcolors[i + k - 1]) {
                cnt++;
            }
            if (cnt == k - 2) {
                res++;
            }
        }
        return res;
    }
}
