class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int>temp;
        for(int i=0;i<n;i++){
            temp.push_back(colors[i]);

        }
        for(int i=0;i<k-1;i++){
            temp.push_back(colors[i]);
        }
        int i=0,j=1;
        int cnt=1;
        int sum=0;
        while(i<n){
            while(cnt<k&&temp[j]==!temp[j-1]){
                cnt++;
                j++;

            }
            if(cnt<k){
                i=j-1;
                j++;
                cnt=1;

            }
            else if(cnt==k){
                sum+=1;
                cnt--;
            }
            i++;

        }
        return sum;



    }
};
