class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors += colors[:(k - 1)]
        pre = [0]
        for i in range(1, len(colors)):
            if colors[i - 1] != colors[i]:
                pre.append(pre[-1] + 1)
            else:
                pre.append(pre[-1])
        #print(f'pre={pre}')
        ans = 0                
        for i in range(k - 1, len(pre)):
            if pre[i] - pre[i - k + 1] == k - 1:
                ans += 1
        return ans
