class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = (int)a.size(); 
        vector<int> v(n);
        int answer = 0;
        for (int i = 0; i < n; i++) {
            if (i == 0) {
                int j = 1;
                while (j < n && a[j] != a[j - 1]) {
                    j++;
                }
                v[i] = j - 1;
                if (v[i] >= k - 1) answer++;
            } else {
                if (v[i - 1] < i) {
                    int j = i + 1; 
                    while (j < n && a[j] != a[j - 1]) {
                        j++;
                    }
                    v[i] = j - 1;
                } else {
                    v[i] = v[i - 1];
                }
                int len = v[i] - i + 1;
                if (v[i] == n - 1) {
                    if (a[n - 1] != a[0]) {
                        len += v[0] + 1;
                    }
                }
                if (len >= k) answer++;
            }
            //cout << i << ' ' << v[i] << endl;
        }
        return answer;
    }
};
