#include <bits/stdc++.h>
using namespace std;
#define siddhantnema ios_base::sync_with_stdio(0); cin.tie(0);
#define ll long long
#define inparr(arr,N) for(ll i=0;i<N;i++){cin>>arr[i];}
#define forn(_i,N) for(ll _i=0;_i<N;_i++)
#define trc(x) cout << #x << "->" << x << '\n';
const ll RMOD = 1000000007;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        //2 ptrs approach
        int N = colors.size();
        int res = 0;
        int p1 = 0,p2 = 1;
        while(p1<N)
        {
            if(colors[p2%N] ^ colors[(p2-1+N)%N])
                p2++;
            else
            {
                p1 = p2;
                p2++;
            }
            if(p2 - p1 == k)
            {
                res++;
                p1++;
            }
        }
        return res;

    }
};
