class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& elements, int length) {
        int size = elements.size();
        for(int i = 0; i < size; i++) elements.push_back(elements[i]);
        vector<int> prefix(2 * size, 0);
        for(int i = 1; i < 2 * size; i++){
            prefix[i] = elements[i] != elements[i - 1];
        }
        for(int i = 1; i < 2 * size; i++) prefix[i] += prefix[i - 1];
        int result = 0;
        for(int i = 0; i < size; i++){
            int sum = prefix[i + length - 1] - prefix[i];
            if(sum == length - 1) result++;
        }      
        return result;
    }
};
