class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int ans = 0;
        List<Integer> colorsList = new ArrayList<>();
        for(int c : colors){
            colorsList.add(c);
        }
        for(int i=0 ; i<k-1 ; i++){
            colorsList.add(colors[i]);
        }
        int[] alternate = new int[colorsList.size()];
        for(int i=1 ; i<colorsList.size(); i++){
            alternate[i] = alternate[i-1] + (
                        colorsList.get(i)!=colorsList.get(i-1) ? 1 : 0
                    );
        }
        for(int i=0 ; i<colors.length ; i++){
            if(alternate[i+k-1]-alternate[i] == k-1){
                ans++;
            }
        }
        return ans;
    }
}
